import styles from "./imageForm.module.css";
import { useRef } from "react";

export const ImageForm = ({ album, onSubmit, mode = "add", initialImage, onCancel }) => {
  const titleInput = useRef();
  const urlInput = useRef();

  const handleSubmit = (e) => {
    e.preventDefault();
    const title = titleInput.current.value.trim();
    const imageurl = urlInput.current.value.trim();
    if (!title || !imageurl) return;

    onSubmit({ title, imageurl });
    handleClear();
  };

  const handleClear = () => {
    titleInput.current.value = "";
    urlInput.current.value = "";
  };

  return (
    <div className={styles.imageForm}>
      <span>Add image to {album.albumName}</span>
      <form onSubmit={handleSubmit}>
        <input required placeholder="Title" ref={titleInput} />
        <input required placeholder="Image URL" ref={urlInput} />

        <div className={styles.actions}>
          <button type="button" onClick={onCancel || handleClear}>
            {mode === "add" ? "Clear" : "Cancel"}
          </button>

          <button type="submit">
            {mode === "add" ? "Add" : "Update"}
          </button>
        </div>
      </form>
    </div>
  );
};
